  $punctuation       =  '[<>#%"]';
  $reserved_range    = q [;/?:@&=];
  $reserved          =  "[$reserved_range]";
  $national          =  '[][{}|\\^~`]';
  $extra             =  "[!*'(),]";
  $safe              =  '[-$_.+]';
  
