  $alphadigit        =  '[a-zA-Z0-9]';              # alpha    | digit
  
  $hex               =  '[a-fA-F0-9]';
  $escape            =  "(?:%$hex$hex)";
  
  $unreserved_range  = q [-a-zA-Z0-9$_.+!*'(),];  # alphadigit | safe | extra
