  use Regexp::Common::URI::RFC1035 qw /$domain/;
  use Regexp::Common::URI::RFC2396 qw /$unreserved $escaped $hex/;
  
  use strict;
  use warnings;
  
  use vars qw /$VERSION/;
  $VERSION = '2010010201';
  
  use vars qw /@EXPORT @EXPORT_OK %EXPORT_TAGS @ISA/;
  
  use Exporter ();
  @ISA = qw /Exporter/;
  
  
