                              "%(?:2[13-7ABDEabde]|3[0-9]|4[1-9A-Fa-f]|" .
                                  "5[AEFaef]|6[0-9A-Fa-f]|7[0-9ACEace]))*)";
  $future_extension  =  "(?:;$token_chars"                       .
                        "(?:=(?:(?:$token_chars(?:[?]$token_chars)?)|" .
                        "$quoted_string))?)";
