  $provider_tag      =  "(?:tsp)";
  $service_provider  =  "(?:;$provider_tag=$provider_hostname)";
  $private_prefix    =  "(?:(?:[!'E-OQ-VX-Z_e-oq-vx-z~]|"                   .
                           "(?:%(?:2[124-7CFcf]|3[AC-Fac-f]|4[05-9A-Fa-f]|" .
                                  "5[1-689A-Fa-f]|6[05-9A-Fa-f]|"           .
                                  "7[1-689A-Ea-e])))"                       .
                           "(?:[!'()*\\-.0-9A-Z_a-z~]+|"                    .
                           "(?:%(?:2[1-9A-Fa-f]|3[AC-Fac-f]|"               .
