  register_uri $nntp_scheme => $nntp_uri;
  
  pattern name    => [qw (URI news)],
          create  => $news_uri,
          ;
  
  pattern name    => [qw (URI NNTP)],
          create  => $nntp_uri,
          ;
  
  1;
  
